function Banner(){this.bannerGroup=$("#banner-group"),this.index=0,this.leftArrow=$(".left-arrow"),this.rightArrow=$(".right-arrow"),this.listenBannerHover()}Banner.prototype.toggleArrow=function(r){if(r){this.leftArrow.show(),this.rightArrow.show()}else this.leftArrow.show(),this.rightArrow.show()},Banner.prototype.listenBannerHover=function(){var r=this;this.bannerGroup.hover(function(){clearInterval(r.timer),r.toggleArrow(!0)},function(){r.loop(),r.toggleArrow(!1)})},Banner.prototype.loop=function(){var r=$("#banner-ul"),n=this;this.timer=setInterval(function(){3<=n.index?n.index=0:n.index++,r.animate({left:-798*n.index},500)},1e3)},Banner.prototype.run=function(){this.loop()},$(function(){(new Banner).run()});